import{_ as i,b as a,a9 as e,o as r}from"./chunks/framework.BjzsCNOl.js";const b=JSON.parse('{"title":"前言","description":"","frontmatter":{},"headers":[],"relativePath":"计算机网络基础/课程笔记/1-前言.md","filePath":"计算机网络基础/课程笔记/1-前言.md","lastUpdated":1738393782000}'),t={name:"计算机网络基础/课程笔记/1-前言.md"};function o(u,l,n,h,s,c){return r(),a("div",null,l[0]||(l[0]=[e('<h1 id="前言" tabindex="-1">前言 <a class="header-anchor" href="#前言" aria-label="Permalink to &quot;前言&quot;">​</a></h1><h2 id="目录" tabindex="-1">目录 <a class="header-anchor" href="#目录" aria-label="Permalink to &quot;目录&quot;">​</a></h2><ul><li><a href="#前言">前言</a><ul><li><a href="#目录">目录</a></li><li><a href="#计算机网络技术的发展">计算机网络技术的发展</a><ul><li><a href="#计算机网络发展历史">计算机网络发展历史</a></li></ul></li><li><a href="#计算机网络体系结构思想">计算机网络体系结构思想</a><ul><li><a href="#网络服务的分层协议实现">网络服务的分层协议实现</a></li><li><a href="#osi-模型">OSI 模型</a></li><li><a href="#tcpip-参考模型">TCP/IP 参考模型</a></li></ul></li><li><a href="#课程基本教学思路及课程说明">课程基本教学思路及课程说明</a><ul><li><a href="#课程基本教学思路">课程基本教学思路</a></li><li><a href="#课程说明">课程说明</a></li><li><a href="#参考书目">参考书目</a></li></ul></li></ul></li></ul><h2 id="计算机网络技术的发展" tabindex="-1">计算机网络技术的发展 <a class="header-anchor" href="#计算机网络技术的发展" aria-label="Permalink to &quot;计算机网络技术的发展&quot;">​</a></h2><h3 id="计算机网络发展历史" tabindex="-1">计算机网络发展历史 <a class="header-anchor" href="#计算机网络发展历史" aria-label="Permalink to &quot;计算机网络发展历史&quot;">​</a></h3><ul><li><b>第一代：<u>面向终端具有通信功能</u>的计算机网络</b><ul><li>40年代，第一台<strong>通用</strong>计算机</li><li>50年代，计算机技术与通信技术相结合，终端通过通信线路连接主机，构成<strong>面向终端</strong>的计算机网络，又称远程联机系统</li><li><strong>中心</strong>节点、<strong>单向</strong>分享</li></ul></li><li><b>第二代：以<u>共享资源</u>为目的的计算机网络</b>。代表：ARPA 网 <ul><li>具备现代计算机网络典型特征：资源的<b>多向共享</b>、<b>分散控制</b>、<u>分组交换</u>、采用专门的通信控制处理机、分层的<u>网络协议</u></li><li>将多台计算机系统通过某种通信手段互连而成的网络</li><li>计算机彼此独立，<strong>没有主从关系</strong></li><li><strong>通信子网</strong>和<strong>资源子网</strong><ul><li>通信子网和资源子网分别建设</li><li>数据通信</li><li>数据处理</li></ul></li><li>通信子网 <ul><li>通信子网由通信控制处理机（CCP）、通信线路等组成 <ul><li>负责完成网络数据传输、转发等通信处理任务</li></ul></li><li>通信控制处理机 <ul><li>在网络拓扑结构中被称为网络结点</li></ul></li><li>通信线路 <ul><li>为通信控制处理机与通信控制处理机、通信控制处理机与主机之间<strong>提供通信信道</strong></li></ul></li></ul></li></ul></li><li>个人计算机与局域网（LAN）出现 <ul><li>20世纪80年代,随着微机的广泛使用,局域网获得了迅速发展。美国电气与电子工程师协会（IEEE）为了适应微机、个人计算机（PC）以及局域网发展的需要,于1980年2月在旧金山成立了 <strong>IEEE802局域网络标准</strong> 委员会，并制定了一系列局域网络标准。</li></ul></li><li><b>第三代：以<u>互连</u>为目的的计算机网络</b><ul><li>协议：主机间通信时对信息的理解、表示形式、应答等需遵守共同的约定</li><li>体系结构：计算机网络的协议分若干层次，各层协议的总和</li></ul></li></ul><h2 id="计算机网络体系结构思想" tabindex="-1">计算机网络体系结构思想 <a class="header-anchor" href="#计算机网络体系结构思想" aria-label="Permalink to &quot;计算机网络体系结构思想&quot;">​</a></h2><h3 id="网络服务的分层协议实现" tabindex="-1">网络服务的分层协议实现 <a class="header-anchor" href="#网络服务的分层协议实现" aria-label="Permalink to &quot;网络服务的分层协议实现&quot;">​</a></h3><p><img src="https://img.chillcicada.com/i/2024/05/26/66533f9d8b23a.webp" alt=""></p><h3 id="osi-模型" tabindex="-1">OSI 模型 <a class="header-anchor" href="#osi-模型" aria-label="Permalink to &quot;OSI 模型&quot;">​</a></h3><ul><li>开放系统互连参考模型 (Open System Interconnect -OSI）</li><li>国际标准化组织 (ISO) 和国际电报电话咨询委员会 (CCITT)</li></ul><p><img src="https://img.chillcicada.com/i/2024/05/26/66533f7c71b14.webp" alt=""></p><h3 id="tcp-ip-参考模型" tabindex="-1">TCP/IP 参考模型 <a class="header-anchor" href="#tcp-ip-参考模型" aria-label="Permalink to &quot;TCP/IP 参考模型&quot;">​</a></h3><p><img src="https://img.chillcicada.com/i/2024/05/26/66533f9200451.webp" alt=""></p><h2 id="课程基本教学思路及课程说明" tabindex="-1">课程基本教学思路及课程说明 <a class="header-anchor" href="#课程基本教学思路及课程说明" aria-label="Permalink to &quot;课程基本教学思路及课程说明&quot;">​</a></h2><h3 id="课程基本教学思路" tabindex="-1">课程基本教学思路 <a class="header-anchor" href="#课程基本教学思路" aria-label="Permalink to &quot;课程基本教学思路&quot;">​</a></h3><ul><li>集中于基本概念与结构，超越技术细节</li><li>学以致用---理论与应用紧密结合 <ul><li>应用 OS 配置</li><li>互联网应用</li><li>网络组建</li></ul></li><li>牢牢把握课程的基本思想 <ul><li>任何一门应用学科都有其独特的解决问题的一套思想与方法</li><li>本课程的核心思想 <ul><li>分层体系 / 服务 —— 效率、灵活性、有序性</li><li>协议</li></ul></li></ul></li></ul><h3 id="课程说明" tabindex="-1">课程说明 <a class="header-anchor" href="#课程说明" aria-label="Permalink to &quot;课程说明&quot;">​</a></h3><ul><li>教学计划</li><li>课程要求 <ul><li>责任与权利均衡原则</li><li>不要影响别人</li></ul></li><li>教学方式 <ul><li>讲授为主，实验、自学、讨论相结合。</li><li>讲授：30小时</li><li>实验：14学时（7次）必选</li></ul></li><li>成绩评定 <ul><li>课堂表现（15）</li><li>实验报告（60）</li><li>综合论文（25）</li></ul></li></ul><h3 id="参考书目" tabindex="-1">参考书目 <a class="header-anchor" href="#参考书目" aria-label="Permalink to &quot;参考书目&quot;">​</a></h3><ul><li>Douglas. E. Comer. 计算机网络与互联网. 机械工业出版社（电子工业出版社）</li><li>谢希仁. 计算机网络. 电子工业出版社</li><li>Douglas. E. Comer. Internet Working With TCP/IP (I, II, III). U.S.A Prentice Hall, 1995</li><li>Andrew S, Tanenbaum. Computer Networks (Fourth edition). U.S.A. Prentice Hall, 2003</li></ul>',21)]))}const f=i(t,[["render",o]]);export{b as __pageData,f as default};
