import{_ as a,b as i,a9 as e,o as t}from"./chunks/framework.BjzsCNOl.js";const p=JSON.parse('{"title":"第十讲：传输层协议","description":"","frontmatter":{},"headers":[],"relativePath":"计算机网络基础/课程笔记/10-传输层协议.md","filePath":"计算机网络基础/课程笔记/10-传输层协议.md","lastUpdated":1738393782000}'),r={name:"计算机网络基础/课程笔记/10-传输层协议.md"};function h(o,l,u,c,n,d){return t(),i("div",null,l[0]||(l[0]=[e('<h1 id="第十讲-传输层协议" tabindex="-1">第十讲：传输层协议 <a class="header-anchor" href="#第十讲-传输层协议" aria-label="Permalink to &quot;第十讲：传输层协议&quot;">​</a></h1><h2 id="目录" tabindex="-1">目录 <a class="header-anchor" href="#目录" aria-label="Permalink to &quot;目录&quot;">​</a></h2><ul><li><a href="#第十讲传输层协议">第十讲：传输层协议</a><ul><li><a href="#目录">目录</a></li><li><a href="#tcp-传输控制协议">TCP 传输控制协议</a><ul><li><a href="#tcp-服务模式">TCP 服务模式</a></li><li><a href="#tcp-数据报的特点">TCP 数据报的特点</a></li><li><a href="#tcp-报文格式">TCP 报文格式</a></li><li><a href="#建立-tcp-连接">建立 TCP 连接</a></li><li><a href="#包丢失与重发">包丢失与重发</a></li><li><a href="#连接释放方式">连接释放方式</a></li></ul></li><li><a href="#udp-用户数据报协议">UDP 用户数据报协议</a><ul><li><a href="#udp-协议">UDP 协议</a></li></ul></li></ul></li></ul><h2 id="tcp-传输控制协议" tabindex="-1">TCP 传输控制协议 <a class="header-anchor" href="#tcp-传输控制协议" aria-label="Permalink to &quot;TCP 传输控制协议&quot;">​</a></h2><h3 id="tcp-服务模式" tabindex="-1">TCP 服务模式 <a class="header-anchor" href="#tcp-服务模式" aria-label="Permalink to &quot;TCP 服务模式&quot;">​</a></h3><ul><li>端对端服务</li><li>虚连接</li><li>TCP 把 IP 看作一个包通信系统</li><li>路由器不需要 TCP 服务</li></ul><h3 id="tcp-数据报的特点" tabindex="-1">TCP 数据报的特点 <a class="header-anchor" href="#tcp-数据报的特点" aria-label="Permalink to &quot;TCP 数据报的特点&quot;">​</a></h3><ul><li><strong>面向连接</strong></li><li><strong>点对点通信</strong></li><li>全双工通信</li><li>无结构的数据流</li><li><strong>完全可靠性</strong></li><li><strong>有缓冲的传送，实现拥塞控制</strong></li></ul><h3 id="tcp-报文格式" tabindex="-1">TCP 报文格式 <a class="header-anchor" href="#tcp-报文格式" aria-label="Permalink to &quot;TCP 报文格式&quot;">​</a></h3><p><img src="https://img.chillcicada.com/i/2024/05/26/66533fe250686.webp" alt=""></p><h3 id="建立-tcp-连接" tabindex="-1">建立 TCP 连接 <a class="header-anchor" href="#建立-tcp-连接" aria-label="Permalink to &quot;建立 TCP 连接&quot;">​</a></h3><p>TCP 协议建立连接的过程就是一个通信双方序号同步的过程</p><ul><li>A -&gt; B SYN —— 我的序号为 x；</li><li>B -&gt; A SYN &amp; ACK —— 我的序号为 y，你的序号为 x + 1；</li><li>A -&gt; B ACK —— 我的序号为 x + 1，你的序号为 y + 1；</li></ul><p><img src="https://img.chillcicada.com/i/2024/05/26/66533fec55f59.webp" alt=""></p><h3 id="包丢失与重发" tabindex="-1">包丢失与重发 <a class="header-anchor" href="#包丢失与重发" aria-label="Permalink to &quot;包丢失与重发&quot;">​</a></h3><ul><li>等待“接收确认”</li><li>超时重发 <ul><li>等待时间的确定</li><li>延迟时间的变化</li></ul></li><li>适应性重发 <ul><li>往返时间的估计加权</li><li>变化量的估计</li></ul></li><li>发送与接收缓冲区</li><li>窗口（剩余缓冲区空间） <ul><li>接收窗口通告</li></ul></li><li>拥塞控制 <ul><li>滑动窗口</li></ul></li></ul><h3 id="连接释放方式" tabindex="-1">连接释放方式 <a class="header-anchor" href="#连接释放方式" aria-label="Permalink to &quot;连接释放方式&quot;">​</a></h3><ul><li>非对称式 <ul><li>任何一方都可以关闭双向连接，存在丢失数据的危险</li></ul></li><li>对称式 <ul><li>每个方向的连接单独关闭，双方都执行 DISCONNECT 才能关闭整条连接</li><li>使用三次握手 + 定时器的方法释放连接，在绝大多数情况下是成功的</li></ul></li></ul><h2 id="udp-用户数据报协议" tabindex="-1">UDP 用户数据报协议 <a class="header-anchor" href="#udp-用户数据报协议" aria-label="Permalink to &quot;UDP 用户数据报协议&quot;">​</a></h2><h3 id="udp-协议" tabindex="-1">UDP 协议 <a class="header-anchor" href="#udp-协议" aria-label="Permalink to &quot;UDP 协议&quot;">​</a></h3><ul><li>简单，无连接</li><li>UDP 增加内容 <ul><li>端口：UDP 能够区分在同一台主机上运行的多个程序</li><li>检验和：确认数据有效</li></ul></li></ul>',21)]))}const P=a(r,[["render",h]]);export{p as __pageData,P as default};
